import React, { useState } from 'react';

export default function Login({ onLogin }) {
  const [user, setUser] = useState('');
  const [pass, setPass] = useState('');

  return (
    <div className="container vh-100 d-flex flex-column justify-content-center align-items-center bg-light">
      <div className="col-md-4 p-4 shadow rounded bg-white">
        <h2 className="mb-4 text-center">Connexion</h2>
        <div className="mb-3">
          <input
            className="form-control"
            placeholder="Utilisateur"
            value={user}
            onChange={e => setUser(e.target.value)}
          />
        </div>
        <div className="mb-3">
          <input
            className="form-control"
            placeholder="Mot de passe"
            type="password"
            value={pass}
            onChange={e => setPass(e.target.value)}
          />
        </div>
        <button className="btn btn-primary w-100" onClick={onLogin}>
          Se connecter
        </button>
      </div>
    </div>
  );
}
