import React, { useEffect, useState } from 'react';

export default function TransactionList() {
  const [txs, setTxs] = useState([]);
  
  useEffect(() => {
    fetch('http://localhost:3001/api/transactions') // Changé de 5000 à 3001
      .then(res => res.json())
      .then(setTxs)
      .catch(err => console.error('Erreur:', err));
  }, []);
  
  return (
    <div>
      <h3>Transactions</h3>
      <ul>
        {txs.map(t => (
          <li key={t.id}>
            User {t.user_id} - Amount: {t.amount} - Status: {t.status}
          </li>
        ))}
      </ul>
    </div>
  );
}
