import React, { useState, useEffect } from 'react';

export default function UserList({ onSelectUser }) {
  const [users, setUsers] = useState([]);
  
  useEffect(() => {
    fetch('http://localhost:3001/api/users') // Changé de 5000 à 3001
      .then(res => res.json())
      .then(setUsers)
      .catch(err => console.error('Erreur:', err));
  }, []);
  
  return (
    <div>
      <h3>Users</h3>
      <ul>
        {users.map(u => (
          <li key={u.id} onClick={() => onSelectUser(u.id)} style={{ cursor: 'pointer' }}>
            {u.name} - {u.email}
          </li>
        ))}
      </ul>
    </div>
  );
}
