import React, { useState } from 'react';

export default function Login({ onLogin }) {
  const [user, setUser] = useState('');
  const [pass, setPass] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleLogin = async () => {
    setLoading(true);
    setError('');
    
    try {
      // CHANGÉ: Utilise le port 3001 au lieu de 5000
      const response = await fetch('http://localhost:3001/api/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username: user, password: pass })
      });
      
      const data = await response.json();
      
      if (data.success) {
        onLogin();
      } else {
        setError('Identifiants incorrects');
      }
    } catch (err) {
      console.error('Erreur:', err);
      setError('Erreur de connexion au serveur');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ maxWidth: '300px', margin: '50px auto', padding: '20px', border: '1px solid #ccc', borderRadius: '8px' }}>
      <h2>Connexion</h2>
      {error && <p style={{ color: 'red', textAlign: 'center' }}>{error}</p>}
      
      <input 
        placeholder="Utilisateur" 
        value={user} 
        onChange={e => setUser(e.target.value)}
        style={{ width: '100%', margin: '10px 0', padding: '8px', boxSizing: 'border-box' }}
      />
      
      <input 
        placeholder="Mot de passe" 
        type="password" 
        value={pass} 
        onChange={e => setPass(e.target.value)}
        style={{ width: '100%', margin: '10px 0', padding: '8px', boxSizing: 'border-box' }}
      />
      
      <button 
        onClick={handleLogin}
        disabled={loading}
        style={{ 
          width: '100%', 
          padding: '10px', 
          backgroundColor: loading ? '#ccc' : '#007bff', 
          color: 'white', 
          border: 'none',
          borderRadius: '4px',
          cursor: loading ? 'not-allowed' : 'pointer'
        }}
      >
        {loading ? 'Connexion...' : 'Se connecter'}
      </button>
      
      <div style={{ marginTop: '10px', fontSize: '12px', color: '#666' }}>
        <p>Identifiants de test :</p>
        <p>Utilisateur: <strong>admin</strong></p>
        <p>Mot de passe: <strong>admin123</strong></p>
      </div>
    </div>
  );
}
